import React, { useState } from "react";
import Calendar from "react-calendar";
import "./Home.css"; // CSS file for styling

const Home = () => {
  const [date, setDate] = useState(new Date());

  const handleCalendarChange = (selectedDate) => {
    setDate(selectedDate);
    // Add your event handling logic here
  };

  const handleCreateMoM = () => {
    // Logic to create a MoM for a selected meeting
    alert("Create MoM button clicked!");
  };

  return (
    <div className="home-container">
      {/* Vertical Navigation Bar */}
      <nav className="vertical-nav">
        <div className="nav-item">
          <input
            type="text"
            placeholder="Search meetings..."
            className="search-bar"
          />
        </div>
        {/* Add more navigation items as needed */}
      </nav>

      {/* Main Content Area */}
      <div className="main-content">
        {/* Horizontal Navigation Bar */}
        <header className="horizontal-nav">
          <button className="create-mom-button" onClick={handleCreateMoM}>
            Create MoM
          </button>
          {/* Add more header elements as needed */}
        </header>

        {/* Calendar Section */}
        <div className="calendar-section">
          <Calendar onChange={handleCalendarChange} value={date} />
        </div>
      </div>
    </div>
  );
};

export default Home;
