// src/components/Home.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './Home.css';

const Home = ({ onLogout }) => {
  const [value, onChange] = useState(new Date());
  const [showDraft, setShowDraft] = useState(false);
  const [notification, setNotification] = useState('');

  // Example user data (you can replace this with actual user data from authentication)
  const username = 'John Doe';
  const email = 'john.doe@example.com';

  const handleDateClick = () => {
    setShowDraft(true); // Show the draft editor when button is clicked
  };

  const handleSubmitDraft = () => {
    setShowDraft(false); // Close the draft editor
    setNotification('Notification submitted!'); // Set the notification message
    setTimeout(() => setNotification(''), 3000); // Clear the notification after 3 seconds
  };

  const tileContent = ({ date, view }) => {
    const today = new Date();
    const isToday =
      date.getDate() === today.getDate() &&
      date.getMonth() === today.getMonth() &&
      date.getFullYear() === today.getFullYear();

    // Only show button on the current date
    if (view === 'month' && isToday) {
      return (
        <button onClick={handleDateClick} className="calendar-button">
          Draft
        </button>
      );
    }
  };

  return (
    <div className="home-container">
      <header className="header">
        <div className="logo-container">
          <img src="logo.png" alt="Logo" className="logo" />
          <span className="app-name">Your App Name</span>
        </div>

        {/* Profile section with username and email */}
        <div className="profile-section">
          <div className="profile-details">
            <span className="profile-name">{username}</span>
            <span className="profile-email">{email}</span>
          </div>
        </div>

        <button className="logout-button" onClick={onLogout}>
          Logout
        </button>
      </header>

      <div className="main-content">
        <Calendar onChange={onChange} value={value} tileContent={tileContent} />

        {showDraft && (
          <div className="draft-editor">
            <h3>Draft Your Note</h3>
            <textarea rows="5" placeholder="Type your draft here..."></textarea>
            <button onClick={handleSubmitDraft}>Submit</button>
          </div>
        )}

        {notification && (
          <div className="notification">
            <p>{notification}</p>
          </div>
        )}
      </div>

      <footer className="footer">Your Footer Content</footer>
    </div>
  );
};

export default Home;
