import React, { useState } from "react";
import Calendar from "react-calendar";
import "./Home.css"; // Import the updated CSS file

const Home = () => {
  const [date, setDate] = useState(new Date());

  const handleCalendarChange = (selectedDate) => {
    setDate(selectedDate);
    // Add your event handling logic here
  };

  const handleLogout = () => {
    // Logic for handling logout
    alert("Logout button clicked!");
  };

  return (
    <div className="home-container">
      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <img
            src="https://via.placeholder.com/40"
            alt="App Logo"
            className="app-logo"
          />
          <span className="app-name">Meeting Manager</span>
        </div>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </header>

      {/* Vertical Navigation Bar */}
      <nav className="vertical-nav">
        <div className="profile-section">
          <img
            src="https://via.placeholder.com/50"
            alt="Profile"
            className="profile-image"
          />
          <div className="profile-details">
            <span className="profile-name"></span>
            <span className="profile-email">john.doe@example.com</span>
          </div>
        </div>
        <div className="nav-item">
          <input
            type="text"
            placeholder="Search meetings..."
            className="search-bar"
          />
        </div>
      </nav>

      {/* Main Content Area */}
      <div className="main-content">
        {/* Calendar Section */}
        <div className="calendar-section">
          <Calendar onChange={handleCalendarChange} value={date} />
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <p>Â© 2024 Meeting Manager. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
